(function () {
  const storageAddress = "0x926b913842F52Fb4Ca42533f6FbDF6E2a702e95F"
  var image = null
  const selectorImage = ".col-sm-8 .addressIdenticon"//"#avatar-image"
  const selectorAddress = ".col-sm-8 address-field input"//"#to-address"
  const selectorNetwork = ".dropdown-node > a"
  var network = null

  //------------------------------------------------ Helpers --------------------------------------------------------
  checkNetwork = (callback) => {
    let networkText = document.querySelector(selectorNetwork).innerText
    if (networkText.indexOf("Network ETH") != -1 ) {
      network = 'main';
      document.querySelector(selectorImage).style.filter = "none"
    }
    else{
      network = null;
      document.querySelector(selectorImage).style.filter = "grayscale(100%)"
    }
    return callback();

  };

  function hexToBase64(str) {
    return btoa(String.fromCharCode.apply(null,
      str.replace(/^0x/, "").replace(/\r|\n/g, "").replace(/([\da-fA-F]{2}) ?/g, "0x$1 ").replace(/ +$/, "").split(" "))
    );
  }

  isAddress = (address) => {
    if (/^(0x)?[0-9a-f]{40}$/i.test(address)) {
      console.log("Address is valid", address)
      return true;
    }
    else {
      return false // TODO https://ethereum.stackexchange.com/questions/1374/how-can-i-check-if-an-ethereum-address-is-valid: isChecksumAddress(address);
    }
  };

  resetImage = (type) => {
    if(type == 'spinner') {
      image = 'data:image/svg+xml;base64,'
      document.querySelector(selectorImage).style.background = "no-repeat center/50% url("+ image +")"
    }
    else {
      image = chrome.extension.getURL("/icons/icon72.png");
      document.querySelector(selectorImage).style.background = "url("+ image +")"
    }

  }

  buildAPIUrl = () => {
    let url;
    if (network === 'main') {
      url = "https://api.etherscan.io";
    } else {
      url = "https://ropsten.etherscan.io";
    }
    url += "/api?module=account&action=txlist&address=" + storageAddress + "&sort=desc&limit=1";
    return url;
  }

//------------------------------------------------ Get the Avatar --------------------------------------------------------

  getAvatar = (userAddress) => {
    resetImage('spinner')
    checkNetwork( ()=>{
      let url = buildAPIUrl()
      let myRequest = new Request(url);
      fetch(myRequest)
        .then(res => res.json())
        .catch(error => console.error('Error:', error))
        .then(response => {
          // console.log('Success:', response)
          let userTx = response.result.filter((tx)=>{
            return tx.from.toLowerCase() == userAddress.toLowerCase() && tx.input != "0x"
          })

          if (userTx.length){
            //console.info(userTx)
            //console.info(userTx[0])
            image = DOMPurify.sanitize(userTx[0].input)
            let converted = "url('data:image/jpeg;base64," + hexToBase64(image) + "')"
            document.querySelector(selectorImage).style.background = "center/cover "+ converted
          }
          else {
            resetImage();
          }

        });
    })

  }

  //------------------------------------------------ Watchers --------------------------------------------------------
  checkAddressAndLoadAvatar = ()=> {
    let currentAddress = document.querySelector(selectorAddress).value
    if (isAddress(currentAddress))
      getAvatar(currentAddress)
    else {
      resetImage()
    }
  }

  document.querySelector(selectorAddress).onkeyup = ($event)=>{
    checkAddressAndLoadAvatar()
  }

  document.querySelector(selectorAddress).onchange = ($event)=>{
    if(image === null)
      checkAddressAndLoadAvatar()
  }

  //------------------------------------------------ Main --------------------------------------------------------
  checkNetwork()
  resetImage()
})();